class Solution:    #To calculate factorial    def factorial(self,n):        if n == 0 or n == 1:            return 1        else:            return (n*self.factorial(n-1))        def combination(self,n,m):        return int(self.factorial(n)/(self.factorial(m)*self.factorial(n-m)))    def paintingPlan(self, n, k):        #LCP 22        res=0        for i in range(n):            #j is the colonne and i is the line of the box            j = (k - i * n)//(n-i)            if j == n:                return 1            #if black box painted == k             if j * (n-i) == k - i * n and j>=0 and j<=n:                res += (self.combination(n,i) * self.combination(n,j))        return ressolu=Solution()print(solu.paintingPlan(3, 6))