class Solution:    #To calculate factorial    def factorial(self,n):        if n == 0 or n == 1:            return 1        else:            return (n*self.factorial(n-1))        def combination(self,n,m):        return int(self.factorial(n)/(self.factorial(m)*self.factorial(n-m)))    def paintingPlan(self, n, k):        #LCP 22        res=0        for i in range(n):            #j 为竖列 i为横行            j = (k - i * n)//(n-i)            if j == n:                return 1            #当且仅当所涂的黑色数目==题目要求涂的黑色数目            if j * (n-i) == k - i * n :                res += (self.combination(n,i) * self.combination(n,j))        return ressolu=Solution()print(solu.paintingPlan(2, 1))